tables.table_schema:
CREATE TABLE GeneSequences (
    id INT PRIMARY KEY,
    sequence VARCHAR(255) NOT NULL
);

CREATE TABLE SequenceAnnotations (
    annotation_id INT PRIMARY KEY,
    sequence_id INT,
    annotation_text TEXT
);

CREATE TABLE SequenceData (
    sequence_id INT PRIMARY KEY,
    sequence_name VARCHAR(255),
    sequence_content TEXT
);

CREATE TABLE GeneInfo (
    gene_id INT PRIMARY KEY,
    gene_symbol VARCHAR(50),
    gene_description TEXT
);

CREATE TABLE SequenceAnalysis (
    analysis_id INT PRIMARY KEY,
    sequence_id INT REFERENCES SequenceData(sequence_id),
    gene_id INT REFERENCES GeneInfo(gene_id),
    analysis_result TEXT
);

CREATE TABLE Samples (
    sample_id INT PRIMARY KEY,
    sample_name VARCHAR(255),
    sequence_id INT,
    FOREIGN KEY (sequence_id) REFERENCES Sequences(sequence_id)
);
