tables.table_schema:
CREATE TABLE bioreactor_data (
  bioreactor_id INT,
  experiment_date DATE,
  temperature FLOAT,
  pressure FLOAT
);

CREATE TABLE cell_growth_data (
  cell_id INT,
  bioreactor_id INT,
  growth_rate FLOAT,
  nutrient_consumption FLOAT
);

CREATE TABLE product_yield_data (
  product_id INT,
  cell_id INT,
  yield FLOAT,
  purity FLOAT
);

CREATE TABLE bioreactor_measurements (
  measurement_id INT PRIMARY KEY,
  measurement_time TIMESTAMP,
  temperature FLOAT,
  pressure FLOAT
);

CREATE TABLE batch_data (
  batch_id INT PRIMARY KEY,
  production_date DATE,
  batch_size INT,
  product_name VARCHAR(50)
);

CREATE TABLE product_quality (
  quality_id INT PRIMARY KEY,
  batch_id INT REFERENCES batch_data(batch_id),
  measurement_id INT REFERENCES bioreactor_measurements(measurement_id),
  quality_score FLOAT
);

CREATE TABLE user_account (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(50),
  created_at TIMESTAMP
);

CREATE TABLE experiment_results (
  result_id INT PRIMARY KEY,
  quality_id INT REFERENCES product_quality(quality_id),
  user_id INT REFERENCES user_account(user_id),
  result_description TEXT
);
