tables.table_schema:
CREATE TABLE KPI_Reports (
   id INT PRIMARY KEY,
   report_name VARCHAR(255),
   kpi_value DECIMAL
);

CREATE TABLE Operations_Data (
   op_id INT PRIMARY KEY,
   operation_name VARCHAR(255),
   metric_value DECIMAL
);

CREATE TABLE Biotech_Units (
   unit_id INT PRIMARY KEY,
   unit_name VARCHAR(255),
   location VARCHAR(255)
);

CREATE TABLE KPI_Reports (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(50),
    report_date DATE
);

CREATE TABLE Operations_Plant (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    location VARCHAR(100),
    start_date DATE
);

CREATE TABLE Production_Equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(50),
    plant_id INT,
    FOREIGN KEY (plant_id) REFERENCES Operations_Plant(plant_id)
);
