    experiment_name VARCHAR,
    experiment_date DATE,
    researcher_id INT
);

CREATE TABLE labs (
  lab_id INT PRIMARY KEY,
  lab_name VARCHAR(255) NOT NULL,
  lab_location VARCHAR(255)
);

CREATE TABLE experiments (
  exp_id INT PRIMARY KEY,
