tables.table_schema:
CREATE TABLE microbial_strains (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    species VARCHAR(50),
    description TEXT
);

CREATE TABLE research_labs (
    id INT PRIMARY KEY,
    lab_name VARCHAR(50),
    location VARCHAR(100),
    researcher_name VARCHAR(50)
);

CREATE TABLE locations (
 id SERIAL PRIMARY KEY,
 location_name VARCHAR(255),
 country VARCHAR(255),
 region VARCHAR(255)
 );
