tables.table_schema:
CREATE TABLE GMO_Origin (
  id SERIAL PRIMARY KEY,
  gmo_id INTEGER REFERENCES GMO_Nutritional_Information(id),
  country VARCHAR(50) NOT NULL,
  region VARCHAR(50)
);

CREATE TABLE GMOs (id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  species VARCHAR(255) NOT NULL,
  genetic_modifications JSONB
);

CREATE TABLE Analysis (id SERIAL PRIMARY KEY,
  gmo_id INT NOT NULL,
  analysis_date DATE NOT NULL,
  result VARCHAR(255),
  FOREIGN KEY (gmo_id) REFERENCES GMOs(id)
);

CREATE TABLE gm_nutrients (
    id INT PRIMARY KEY,
    food_id INT NOT NULL,
    nutrient_name VARCHAR(50) NOT NULL,
    value DECIMAL(5,2) NOT NULL
);

CREATE TABLE gm_allergens (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE gm_reviews (
    id INT PRIMARY KEY,
    food_id INT NOT NULL,
    rating DECIMAL(3,1) NOT NULL,
    review_text TEXT
);
