tables.table_schema:
CREATE TABLE gene_editing_experiments (
    experiment_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    experiment_date DATE,
    modified_genes TEXT
);

CREATE TABLE regulations_compliance (
    regulation_id INT PRIMARY KEY,
    experiment_id INT,
    compliance_status BOOLEAN,
    regulation_notes TEXT
);

CREATE TABLE experiment_results (
    result_id SERIAL PRIMARY KEY,
    experiment_id INT REFERENCES gene_editing_experiments(experiment_id),
    gene_expression_level FLOAT NOT NULL,
    toxicity_level FLOAT NOT NULL
);

CREATE TABLE regulatory_compliance (
    compliance_id SERIAL PRIMARY KEY,
    experiment_id INT REFERENCES gene_editing_experiments(experiment_id),
    compliance_status VARCHAR(50) NOT NULL
);

CREATE TABLE regulations (
    id UUID PRIMARY KEY,
    regulation_number VARCHAR(50),
    regulation_description TEXT
);
