tables.table_schema:
CREATE TABLE Researchers (
    researcher_id SERIAL PRIMARY KEY,
    researcher_name VARCHAR(255),
    department VARCHAR(100),
    email VARCHAR(255)
);

CREATE TABLE research_findings (
  id INT PRIMARY KEY,
  research_title TEXT,
  research_date DATE,
  findings TEXT
);

CREATE TABLE food_allergies (
  id INT PRIMARY KEY,
  allergy_name TEXT,
  allergy_severity TEXT,
  allergy_triggers TEXT
);

CREATE TABLE genetic_modification (
  id INT PRIMARY KEY,
  modified_food TEXT,
  modification_technique TEXT,
  impact_description TEXT
);

CREATE TABLE IF NOT EXISTS food_allergies (
  allergy_id INT PRIMARY KEY,
  allergen_name VARCHAR(50) NOT NULL,
  allergy_type VARCHAR(50),
  allergic_reaction TEXT
);

CREATE TABLE IF NOT EXISTS genetic_modification (
  modification_id INT PRIMARY KEY,
  modification_type VARCHAR(50) NOT NULL,
  modification_description TEXT
);

CREATE TABLE IF NOT EXISTS research_authors (
  author_id INT PRIMARY KEY,
  study_id INT REFERENCES research_studies(study_id),
  author_name VARCHAR(100) NOT NULL
);
