tables.table_schema:
CREATE TABLE Farmers (
 farmer_id UUID PRIMARY KEY,
 farmer_name VARCHAR(255) NOT NULL,
 location VARCHAR(255) NOT NULL
);

CREATE TABLE Seeds (
 seed_id UUID PRIMARY KEY,
 seed_name VARCHAR(255) NOT NULL,
 genetic_info VARCHAR(255) NOT NULL,
 quantity INT NOT NULL
);

CREATE TABLE farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50),
    farmer_location VARCHAR(100)
);

CREATE TABLE seeds (
    seed_id INT PRIMARY KEY,
    seed_name VARCHAR(50),
    seed_description VARCHAR(200)
);
