tables.table_schema:
CREATE TABLE biotech_food_processing_stages (
    stage_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    process_description TEXT,
    quality_checks VARCHAR(100)
);

CREATE TABLE quality_control_records (
  id INT PRIMARY KEY,
  stage_name VARCHAR(50),
  date_processed DATE,
  quality_rating INT
);

CREATE TABLE food_products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(100),
  ingredients TEXT,
  allergens VARCHAR(100)
);

CREATE TABLE processing_stages (
  stage_id INT PRIMARY KEY,
  stage_name VARCHAR(50),
  critical_points TEXT,
  process_duration INT
);

CREATE TABLE product_safety (
  product_id INT,
  stage_id INT,
  has_passed BOOLEAN,
  remarks TEXT
);

CREATE TABLE processing_stages (
    stage_id INT PRIMARY KEY,
    stage_name VARCHAR(100),
    description TEXT
);

CREATE TABLE product_safety (
    safety_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    safety_rating VARCHAR(50),
    safety_tested_on DATE
);

CREATE TABLE employee_records (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(255),
    department VARCHAR(100),
    position VARCHAR(100)
);

CREATE TABLE department_records (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(100),
    supervisor VARCHAR(255),
    location VARCHAR(100)
);
