tables.table_schema:
CREATE TABLE Bioengineered_Plants (
  plant_id SERIAL PRIMARY KEY,
  plant_name VARCHAR(100) NOT NULL,
  genetic_traits TEXT,
  breeding_program_id INT
);

CREATE TABLE Breeding_Programs (
  breeding_program_id SERIAL PRIMARY KEY,
  program_name VARCHAR(100) NOT NULL,
  program_description TEXT,
  lead_scientist VARCHAR(50)
);

CREATE TABLE PlantTraits (
  plant_id UUID PRIMARY KEY,
  trait_name VARCHAR(255),
  trait_value VARCHAR(255),
  observation_date TIMESTAMP
);

CREATE TABLE BreedingPrograms (
  program_id UUID PRIMARY KEY,
  program_name VARCHAR(255) UNIQUE,
  start_date DATE,
  end_date DATE
);

CREATE TABLE PlantBreeding (
  breeding_id UUID PRIMARY KEY,
  plant_id UUID REFERENCES PlantTraits(plant_id),
  program_id UUID REFERENCES BreedingPrograms(program_id),
  breeding_date TIMESTAMP
);

CREATE TABLE Plants (
    plant_id SERIAL PRIMARY KEY,
    plant_name VARCHAR(255) NOT NULL,
    genetic_traits TEXT,
    growth_rate FLOAT
);

CREATE TABLE Traits (
    trait_id SERIAL PRIMARY KEY,
    trait_name VARCHAR(255) NOT NULL,
    trait_description TEXT
);

CREATE TABLE BreedingPrograms (
    program_id SERIAL PRIMARY KEY,
    program_name VARCHAR(255) NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE PlantTraits (
    plant_id INTEGER REFERENCES Plants(plant_id),
    trait_id INTEGER REFERENCES Traits(trait_id),
    trait_value TEXT
);
