CREATE TABLE pest_occurrences (
    id INT PRIMARY KEY,
    pest_name VARCHAR(255),
    occurrence_date TIMESTAMP,
    agricultural_field_id INT
);

CREATE TABLE disease_occurrences (
    id INT PRIMARY KEY,
    disease_name VARCHAR(255),
    occurrence_date TIMESTAMP,
    agricultural_field_id INT
);

CREATE TABLE agricultural_fields (
    id INT PRIMARY KEY,
    field_name VARCHAR(255),
    field_location VARCHAR(255),
    acreage FLOAT
);

CREATE TABLE Pest { 
  id INT PRIMARY KEY, 
  name VARCHAR(50) NOT NULL, 
  type VARCHAR(50) NOT NULL, 
  affected_crops TEXT 
}

CREATE TABLE Disease { 
  id INT PRIMARY KEY, 
  name VARCHAR(50) NOT NULL, 
  symptoms TEXT, 
 treatment TEXT 
}

CREATE TABLE Field { 
  id INT PRIMARY KEY, 
  field_name VARCHAR(100) NOT NULL, 
  location VARCHAR(100) NOT NULL, 
 owner_name VARCHAR(100) NOT NULL 
}

CREATE TABLE PestOccurrence { 
  id INT PRIMARY KEY, 
  pest_id INT REFERENCES Pest(id), 
 field_id INT REFERENCES Field(id), 
 occurrence_date DATE NOT NULL 
}

CREATE TABLE DiseaseOccurrence { 
  id INT PRIMARY KEY, 
  disease_id INT REFERENCES Disease(id), 
 field_id INT REFERENCES Field(id), 
 occurrence_date DATE NOT NULL 
}
