tables.table_schema:
CREATE TABLE growth_rate_history ( 
  id INT PRIMARY KEY, 
  particle_id INT, 
  growth_rate FLOAT, 
  measurement_time TIMESTAMP 
);

CREATE TABLE NanoParticles ( 
  particle_id INT PRIMARY KEY, 
  particle_name VARCHAR(255), 
  growth_rate FLOAT, 
  measurement_date DATE 
);

CREATE TABLE ParticleResearcher ( 
  researcher_id INT PRIMARY KEY, 
  researcher_name VARCHAR(255), 
  department VARCHAR(255) 
);

CREATE TABLE Particle (
    particle_id INT PRIMARY KEY,
    size INT,
    rate FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE Lab (
    lab_id INT PRIMARY KEY,
    lab_name VARCHAR(255)
);

CREATE TABLE Researcher (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    lab_id INT,
    FOREIGN KEY (lab_id) REFERENCES Lab(lab_id)
);

CREATE TABLE NanoParticles (
  particle_id INT PRIMARY KEY,
  size FLOAT NOT NULL,
  growth_rate FLOAT NOT NULL,
  measurement_unit VARCHAR(10) NOT NULL
);

CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50) NOT NULL,
  department VARCHAR(50) NOT NULL
);

CREATE TABLE Apparatus (
  apparatus_id INT PRIMARY KEY,
  apparatus_name VARCHAR(50) NOT NULL,
  location VARCHAR(50) NOT NULL
);
