tables.table_schema:
CREATE TABLE Nanostructures (
    id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    size DECIMAL NOT NULL,
    shape TEXT NOT NULL,
    composition TEXT
);
CREATE TABLE NanostructuresComparison (
    comparison_id SERIAL PRIMARY KEY,
    nanostructure1_id INT REFERENCES Nanostructures(id),
    nanostructure2_id INT REFERENCES Nanostructures(id),
    similarity_score DECIMAL
);

CREATE TABLE nanostructure_size (
  id SERIAL PRIMARY KEY,
  length FLOAT,
  width FLOAT,
  height FLOAT
);

CREATE TABLE nanostructure_shape (
  id SERIAL PRIMARY KEY,
  shape_name VARCHAR(50),
  aspect_ratio FLOAT,
  surface_area FLOAT
);

CREATE TABLE nanostructure_composition (
  id SERIAL PRIMARY KEY,
  material_name VARCHAR(100),
  chemical_formula TEXT,
  density FLOAT
);
