tables.table_schema:
CREATE TABLE toxicity_research_findings (
  id INT PRIMARY KEY,
  material_name VARCHAR(255),
  toxicity_level VARCHAR(50),
  research_date DATE
);

CREATE TABLE toxic_materials (
 id INT PRIMARY KEY,
 material_name VARCHAR(255) NOT NULL,
 toxicity_level VARCHAR(50) NOT NULL
);

CREATE TABLE NanoMaterials (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  toxicity_level VARCHAR(20),
  researcher_id INT
);

CREATE TABLE Researchers (
  id INT PRIMARY KEY,
  full_name VARCHAR(100),
  department VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE ResearchStudios (
  id INT PRIMARY KEY,
  location VARCHAR(100),
  capacity INT,
  professor_id INT
);
