tables.table_schema:
CREATE TABLE experiment_records (
    experiment_id INT PRIMARY KEY,
    nanostructure_id INT,
    biomolecule_id INT,
    interaction_result TEXT
);

CREATE TABLE nanostructures (
    nanostructure_id INT PRIMARY KEY,
    nanostructure_name TEXT,
    nanostructure_material TEXT
);

CREATE TABLE nanostructures (
  nanostructure_id SERIAL PRIMARY KEY,
  nanostructure_name VARCHAR(100) NOT NULL,
  nanostructure_type VARCHAR(50) NOT NULL,
  nanostructure_size INT
);

CREATE TABLE biomolecules (
  biomolecule_id SERIAL PRIMARY KEY,
  biomolecule_name VARCHAR(100) NOT NULL,
  biomolecule_type VARCHAR(50) NOT NULL,
  biomolecule_sequence TEXT
);

CREATE TABLE experiments (
  experiment_id INT PRIMARY KEY,
  nanostructure_id INT,
  biomolecule_id INT,
  experiment_date DATE
);

CREATE TABLE nanostructures (
  nanostructure_id INT PRIMARY KEY,
  nanostructure_name VARCHAR(50),
  nanostructure_type VARCHAR(50),
  material_composition VARCHAR(50)
);

CREATE TABLE biomolecules (
  biomolecule_id INT PRIMARY KEY,
  biomolecule_name VARCHAR(50),
  biomolecule_type VARCHAR(50),
  molecular_weight FLOAT
);
