tables.table_schema:
CREATE TABLE NanoMaterial (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50),
  stability_rating FLOAT
);

CREATE TABLE StorageConditions (
  condition_id INT PRIMARY KEY,
  condition_name VARCHAR(50),
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE StabilityReport (
  report_id INT PRIMARY KEY,
  control_id INT,
  stability_score FLOAT,
  stability_notes TEXT
);

CREATE TABLE TestConditions (
    condition_id INT PRIMARY KEY,
    condition_name TEXT,
    description TEXT
);

CREATE TABLE MaterialImages (
    image_id INT PRIMARY KEY,
    material_id INT,
    image_url TEXT,
    FOREIGN KEY (material_id) REFERENCES NanoMaterials(id)
);
