tables.table_schema:
CREATE TABLE dna_sequences (
    id UUID PRIMARY KEY,
    sequence TEXT NOT NULL
);

CREATE TABLE sequence_searches (
    search_id UUID PRIMARY KEY,
    sequence_id UUID,
    search_result TEXT
);

CREATE TABLE DNA_Sequences (
    id INT PRIMARY KEY,
    sequence VARCHAR(255) NOT NULL
);

CREATE TABLE Sequence_Types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50) NOT NULL
);

CREATE TABLE dna_sequences (
    id SERIAL PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE genes (
    id SERIAL PRIMARY KEY,
    gene_name VARCHAR(255),
    sequence_id INT,
    FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);

CREATE TABLE proteins (
    id SERIAL PRIMARY KEY,
    protein_name VARCHAR(255),
    sequence_id INT,
    FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);

CREATE TABLE genomes (
    id SERIAL PRIMARY KEY,
    genome_name VARCHAR(255),
    sequence_id INT,
    FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);
