tables.table_schema:
CREATE TABLE BiodiversityData (
  id INT PRIMARY KEY,
  species_name VARCHAR(50),
  ecosystem VARCHAR(50),
  population_count INT
);

CREATE TABLE species (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    scientific_name VARCHAR(255) NOT NULL,
    ecosystem_id INT REFERENCES ecosystems(id)
);

CREATE TABLE ecosystems (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE species_population (
    species_id INT REFERENCES species(id),
    ecosystem_id INT REFERENCES ecosystems(id),
    population_count INT NOT NULL,
    last_observed DATE
);

CREATE TABLE Ecosystems (
  ecosystem_id INT PRIMARY KEY,
  ecosystem_name VARCHAR(50) NOT NULL,
  location VARCHAR(100),
  habitat_type VARCHAR(50)
);

CREATE TABLE Observations (
  observation_id INT PRIMARY KEY,
  species_id INT,
  observation_date DATE,
  observer_name VARCHAR(50)
);

CREATE TABLE Habitats (
  habitat_id INT PRIMARY KEY,
  habitat_name VARCHAR(50) NOT NULL,
  ecosystem_id INT
);

CREATE TABLE Species (
  id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  ecosystem_id INT,
  FOREIGN KEY (ecosystem_id) REFERENCES Ecosystems(id)
);

CREATE TABLE Ecosystems (
  id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL
);

CREATE TABLE Researchers (
  id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  specialization VARCHAR(100)
);
