tables.table_schema:
CREATE TABLE pharmacogenomics_data (
  patient_id INT,
  genetic_info VARCHAR(255),
  drug_name VARCHAR(255),
  dosage FLOAT
);

CREATE TABLE medications (
    medication_id UUID PRIMARY KEY,
    medication_name VARCHAR(50),
    dosage VARCHAR(20),
    patient_id UUID
);

CREATE TABLE patient_genetics (
    patient_id INT PRIMARY KEY,
    genetic_info TEXT
);

CREATE TABLE drug_list (
    drug_id INT PRIMARY KEY,
    drug_name TEXT,
    dosage INT
);

CREATE TABLE Patients (
    patient_id INT PRIMARY KEY,
    patient_name TEXT,
    genetic_information JSON
);

CREATE TABLE Drugs (
    drug_id INT PRIMARY KEY,
    drug_name TEXT,
    drug_type TEXT
);

CREATE TABLE Treatments (
    treatment_id INT PRIMARY KEY,
    patient_id INT,
    drug_id INT,
    dosage FLOAT
);

CREATE TABLE GeneticVariants (
    variant_id INT PRIMARY KEY,
    gene_name TEXT,
    variant_type TEXT
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255),
    genetic_information TEXT
);

CREATE TABLE Drug (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(255),
    dosage INT
);

CREATE TABLE Prescription (
    prescription_id INT PRIMARY KEY,
    patient_id INT,
    drug_id INT,
    prescription_date DATE
);

CREATE TABLE Genotype (
    genotype_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    allele VARCHAR(10)
);

CREATE TABLE DrugInteraction (
    interaction_id INT PRIMARY KEY,
    drug1_id INT,
    drug2_id INT,
    interaction_description TEXT
);
