tables.table_schema:
CREATE TABLE sample_data (
    sample_id INT PRIMARY KEY,
    environment_id INT,
    microbial_species TEXT,
    abundance_percent DECIMAL
);

CREATE TABLE environment_data (
    environment_id INT PRIMARY KEY,
    environment_name TEXT,
    temperature DECIMAL,
    humidity DECIMAL
);

CREATE TABLE MetagenomicSamples (
    sample_id UUID PRIMARY KEY,
    sample_name VARCHAR(255),
    environment VARCHAR(255),
    collection_date DATE
);

CREATE TABLE MicrobialSpecies (
    species_id UUID PRIMARY KEY,
    species_name VARCHAR(255),
    genus VARCHAR(255),
    phylum VARCHAR(255)
);

CREATE TABLE environment_samples (
    sample_id INT PRIMARY KEY,
    sample_name VARCHAR(50) NOT NULL,
    environment_type VARCHAR(50) NOT NULL,
    collection_date DATE
);

CREATE TABLE microbial_species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL,
    species_type VARCHAR(50) NOT NULL,
    abundance FLOAT
);
