tables.table_schema:
CREATE TABLE Species (
    species_id UUID PRIMARY KEY,
    species_name STRING,
    common_name STRING,
    classification STRING
);

CREATE TABLE Genus (
    genus_id UUID PRIMARY KEY,
    genus_name STRING,
    family_name STRING
);

CREATE TABLE Family (
    family_id UUID PRIMARY KEY,
    family_name STRING,
    order_name STRING
);

CREATE TABLE species (species_id INT PRIMARY KEY, species_name TEXT, family_id INT);
CREATE TABLE family (family_id INT PRIMARY KEY, family_name TEXT);
CREATE TABLE genus (genus_id INT PRIMARY KEY, genus_name TEXT, family_id INT);
CREATE TABLE phylogeny (phylogeny_id INT PRIMARY KEY, species_id INT, ancestor_id INT);
