tables.table_schema:
CREATE TABLE genetic_markers (
    marker_id INT PRIMARY KEY,
    marker_name VARCHAR(50),
    trait_associated VARCHAR(50)
);

CREATE TABLE plant_traits (
    trait_id INT PRIMARY KEY,
    trait_name VARCHAR(50),
    plant_name VARCHAR(50),
    trait_description TEXT
);

CREATE TABLE plant_traits (
  marker_id INT PRIMARY KEY,
  trait_name VARCHAR(255) NOT NULL
);

CREATE TABLE plant_markers (
  marker_id INT PRIMARY KEY,
  chromosome_id INT NOT NULL,
  position INT NOT NULL,
  gene_name VARCHAR(255) NOT NULL
);

CREATE TABLE plant_genealogy (
  plant_id INT PRIMARY KEY,
  parent_id INT,
  generation INT
);

CREATE TABLE plant_traits (
    id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    trait VARCHAR(50),
    genetic_marker VARCHAR(50)
);

CREATE TABLE marker_info (
    marker_id INT PRIMARY KEY,
    marker_name VARCHAR(50),
    chromosome_no INT,
    location VARCHAR(50)
);

CREATE TABLE scientist (
    scientist_id INT PRIMARY KEY,
    scientist_name VARCHAR(50),
    specialty VARCHAR(50)
);

CREATE TABLE trait_category (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE plant_marker_relation (
    plant_id INT,
    marker_id INT,
    FOREIGN KEY (plant_id) REFERENCES plant_traits(id),
    FOREIGN KEY (marker_id) REFERENCES marker_info(marker_id)
);

CREATE TABLE scientist_research (
    scientist_id INT,
    category_id INT,
    FOREIGN KEY (scientist_id) REFERENCES scientist(scientist_id),
    FOREIGN KEY (category_id) REFERENCES trait_category(category_id)
);
