tables.table_schema:
CREATE TABLE GeneticTraits (
  trait_id INT PRIMARY KEY,
  trait_name VARCHAR(255),
  trait_description TEXT
);

CREATE TABLE Genes (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(50) NOT NULL,
    inheritance_type VARCHAR(20) NOT NULL
);

CREATE TABLE Individuals (
    individual_id INT PRIMARY KEY,
    individual_name VARCHAR(50) NOT NULL,
    date_of_birth DATE NOT NULL,
    gender VARCHAR(10) NOT NULL
);

CREATE TABLE Traits (
    trait_id INT PRIMARY KEY,
    trait_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE genetic_traits (
  trait_id INT PRIMARY KEY,
  trait_name VARCHAR(50) NOT NULL,
  trait_description TEXT
);

CREATE TABLE inheritance_patterns (
  pattern_id INT PRIMARY KEY,
  pattern_type VARCHAR(50) NOT NULL,
  pattern_description TEXT
);

CREATE TABLE families (
  family_id INT PRIMARY KEY,
  family_name VARCHAR(50) NOT NULL,
  location VARCHAR(100)
);

CREATE TABLE individuals (
  individual_id INT PRIMARY KEY,
  individual_name VARCHAR(50) NOT NULL,
  gender VARCHAR(10),
  age INT
);
