tables.table_schema:
CREATE TABLE BacteriaStrains (
    StrainID INT PRIMARY KEY,
    StrainName VARCHAR(50),
    GenomicSequence TEXT
);

CREATE TABLE bacteria_strains (
    id INT PRIMARY KEY,
    strain_name VARCHAR(255),
    genetic_sequence TEXT
);

CREATE TABLE researcher (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    institution VARCHAR(255)
);

CREATE TABLE comparison_results (
    id INT PRIMARY KEY,
    bacteria_strain_id INT,
    researcher_id INT,
    similarity_score FLOAT
);

CREATE TABLE BacteriaStrains (
    bacteria_id INT PRIMARY KEY,
    bacteria_name VARCHAR(50),
    sequence TEXT
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50)
);

CREATE TABLE Sequences (
    sequence_id INT PRIMARY KEY,
    bacteria_id INT,
    sequence_data TEXT,
    FOREIGN KEY (bacteria_id) REFERENCES BacteriaStrains(bacteria_id)
);
