tables.table_schema:
CREATE TABLE target_species_population_genetics (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50),
  population_size INT,
  genetic_variation DECIMAL(5,2) 
);

CREATE TABLE population_genetics (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255),
  population_size INT,
  allele_frequency FLOAT
);

CREATE TABLE genetic_variants (
  variant_id INT PRIMARY KEY,
  variant_name VARCHAR(255),
  variant_type VARCHAR(50),
  variant_frequency FLOAT
);

CREATE TABLE population_genetics (
 proj_id INT PRIMARY KEY,
 proj_name VARCHAR(50),
 species_type VARCHAR(50),
 population_size INT
);

CREATE TABLE Species (
	id SERIAL PRIMARY KEY,
	species_name VARCHAR(100) NOT NULL,
	population_size INT,
	average_lifespan INT
);

CREATE TABLE Genotypes (
	id SERIAL PRIMARY KEY,
	individual_id INT NOT NULL,
	genotype VARCHAR(50) NOT NULL,
	mutation_rate FLOAT
);

CREATE TABLE Phenotypes (
	id SERIAL PRIMARY KEY,
	individual_id INT NOT NULL,
	phenotype VARCHAR(50) NOT NULL,
	expressed BOOLEAN
);
