tables.table_schema:
CREATE TABLE EndangeredSpecies ( 
    species_id INT PRIMARY KEY, 
    species_name VARCHAR(50) NOT NULL, 
    conservation_status VARCHAR(20) NOT NULL 
);

CREATE TABLE GeneticData ( 
    data_id INT PRIMARY KEY, 
    species_id INT, 
    dna_sequence TEXT NOT NULL, 
    mutation_rate FLOAT 
    FOREIGN KEY (species_id) REFERENCES EndangeredSpecies(species_id) 
);

CREATE TABLE endangered_species (
  id UUID PRIMARY KEY,
  species_name STRING,
  conservation_status STRING,
  habitat STRING
);

CREATE TABLE researcher (
  id UUID PRIMARY KEY,
  researcher_name STRING,
  affiliation STRING
);

CREATE TABLE EndangeredSpecies (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255) NOT NULL,
  conservation_status VARCHAR(50),
  habitat_description TEXT
);

CREATE TABLE GeneticData (
  data_id INT PRIMARY KEY,
  species_id INT,
  genetic_sequence TEXT,
  data_source VARCHAR(100)
);

CREATE TABLE Researcher (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(255) NOT NULL,
  affiliation VARCHAR(255),
  research_area VARCHAR(100)
);

CREATE TABLE ConservationEfforts (
  effort_id INT PRIMARY KEY,
  species_id INT,
  conservation_method VARCHAR(100),
  success_rate DECIMAL(5,2)
);
