tables.table_schema:
CREATE TABLE Trials (
   trial_id INT PRIMARY KEY,
   trial_name VARCHAR(255),
   start_date DATE,
   end_date DATE
);

CREATE TABLE ClinicalTrial (
  trial_id SERIAL PRIMARY KEY,
  trial_name TEXT,
  start_date DATE,
  end_date DATE
);

CREATE TABLE TrialData (
  data_id SERIAL PRIMARY KEY,
  patient_id INT REFERENCES Patient(patient_id),
  trial_id INT REFERENCES ClinicalTrial(trial_id),
  result TEXT
);

CREATE TABLE patients (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE trials (
  trial_id INT PRIMARY KEY,
  trial_name VARCHAR(100),
  start_date DATE,
  end_date DATE
);

CREATE TABLE medications (
  med_id INT PRIMARY KEY,
  med_name VARCHAR(100),
  dosage FLOAT,
  frequency VARCHAR(20)
);

CREATE TABLE doctors (
  doc_id INT PRIMARY KEY,
  doc_name VARCHAR(50),
  specialty VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE patient_trials (
  patient_id INT,
  trial_id INT,
  enrollment_date DATE,
  PRIMARY KEY (patient_id, trial_id)
);
