tables.table_schema:
CREATE TABLE adverse_event_reports (
  report_id UUID PRIMARY KEY,
  company_id UUID NOT NULL,
  event_description TEXT,
  report_date TIMESTAMP
);

CREATE TABLE regulatory_authorities (
  authority_id UUID PRIMARY KEY,
  authority_name TEXT
);

CREATE TABLE reports (
 report_id INT PRIMARY KEY,
 company_name VARCHAR(255),
 event_description TEXT,
 event_date DATE
);

CREATE TABLE regulatory_authorities (
 authority_id INT PRIMARY KEY,
 authority_name VARCHAR(255),
 contact_person VARCHAR(255),
 contact_email VARCHAR(255)
);

CREATE TABLE company_contacts (
 contact_id INT PRIMARY KEY,
 company_name VARCHAR(255),
 contact_person VARCHAR(255),
 contact_email VARCHAR(255)
);

CREATE TABLE adverse_events (
    event_id SERIAL PRIMARY KEY,
    reporter_name VARCHAR(50),
    event_description TEXT,
    event_date DATE
);

CREATE TABLE regulatory_authorities (
    authority_id SERIAL PRIMARY KEY,
    authority_name VARCHAR(50),
    contact_person_name VARCHAR(50),
    contact_email VARCHAR(50)
);

CREATE TABLE event_reports (
    report_id SERIAL PRIMARY KEY,
    event_id INT REFERENCES adverse_events(event_id),
    authority_id INT REFERENCES regulatory_authorities(authority_id),
    report_date DATE
);

CREATE TABLE communication_logs (
    log_id SERIAL PRIMARY KEY,
    report_id INT REFERENCES event_reports(report_id),
    communication_date DATE,
    communication_details TEXT
);
