tables.table_schema:
CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50) NOT NULL,
  researcher_email VARCHAR(50) UNIQUE
);

CREATE TABLE GenomicsData (
  data_id INT PRIMARY KEY,
  researcher_id INT REFERENCES Researchers(researcher_id),
  genomics_data TEXT NOT NULL, 
  data_timestamp TIMESTAMP
);

CREATE TABLE researcher_research (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255) NOT NULL
);

CREATE TABLE genomics_data (
    data_id INT PRIMARY KEY,
    researcher_id INT NOT NULL,
    data_type VARCHAR(100) NOT NULL,
    data_content TEXT
);

CREATE TABLE drug_targets (
    target_id INT PRIMARY KEY,
    researcher_id INT NOT NULL,
    gene_name VARCHAR(100) NOT NULL,
    target_name VARCHAR(255) NOT NULL
);

CREATE TABLE Researchers (
	researcher_id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	affiliation VARCHAR(255) NOT NULL
);

CREATE TABLE GenomicsData (
	data_id INT PRIMARY KEY,
	dna_sequence TEXT NOT NULL,
	rna_expression FLOAT
);

CREATE TABLE DrugTargets (
	target_id INT PRIMARY KEY,
	target_name VARCHAR(255) NOT NULL,
	target_gene VARCHAR(50) NOT NULL,
	action_mechanism VARCHAR(100)
);

CREATE TABLE BiopharmaceuticalDevelopment (
	development_id INT PRIMARY KEY,
	drug_name VARCHAR(255) NOT NULL,
	target_id INT NOT NULL,
	FOREIGN KEY (target_id) REFERENCES DrugTargets(target_id)
);

CREATE TABLE researcher (id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR);
