tables.table_schema:
CREATE TABLE compounds (
    compound_id SERIAL PRIMARY KEY,
    compound_name VARCHAR(255) NOT NULL,
    molecular_formula VARCHAR(50) NOT NULL,
    testing_date DATE
);

CREATE TABLE research_studies (
    study_id SERIAL PRIMARY KEY,
    study_name VARCHAR(100) NOT NULL,
    compound_id INT,
    effectiveness_score FLOAT
);

CREATE TABLE compounds (
    compound_id INT PRIMARY KEY,
    compound_name TEXT,
    tested_on DATE
);

CREATE TABLE studies (
    study_id INT PRIMARY KEY,
    study_name TEXT,
    start_date DATE,
    end_date DATE
);
