tables.table_schema:
CREATE TABLE patents (
    patent_id UUID PRIMARY KEY,
    patent_title VARCHAR,
    patent_description TEXT,
    patent_owner VARCHAR
);

CREATE TABLE products (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR,
    product_description TEXT,
    patent_id UUID REFERENCES patents(patent_id)
);

CREATE TABLE biopharmaceutical_products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    product_description TEXT,
    manufacturer_name VARCHAR(100)
);

CREATE TABLE patent_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(255),
    owner_country VARCHAR(50)
);

CREATE TABLE patent_assignees (
    assignee_id INT PRIMARY KEY,
    patent_id INT,
    assignee_name VARCHAR(255),
    assignment_date DATE
);

CREATE TABLE patent_citations (
    citation_id INT PRIMARY KEY,
    citing_patent_id INT,
    cited_patent_id INT,
    citation_date DATE
);
