tables.table_schema:
CREATE TABLE publications (
    publication_id INT PRIMARY KEY,
    publication_title VARCHAR(255),
    publication_date DATE,
    author_name VARCHAR(255),
    research_project_id INT
);

CREATE TABLE research_projects (
    research_project_id INT PRIMARY KEY,
    project_title VARCHAR(255),
    project_description TEXT,
    research_type VARCHAR(50)
);

CREATE TABLE ResearchProjects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    project_description TEXT,
    researcher_id INT
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    researcher_affiliation VARCHAR(255),
    researcher_email VARCHAR(255)
);

CREATE TABLE Literature (
    publication_id INT PRIMARY KEY,
    publication_title VARCHAR(255),
    publication_year INT,
    publication_abstract TEXT
);
