tables.table_schema:
CREATE TABLE experimental_protocols (
 protocol_id INT PRIMARY KEY,
 protocol_name VARCHAR(255),
 protocol_description TEXT
);

CREATE TABLE research_findings (
 finding_id INT PRIMARY KEY,
 protocol_id INT,
 finding_date DATE,
 finding_description TEXT
);

CREATE TABLE biotechnology_projects (
 project_id INT PRIMARY KEY,
 project_name VARCHAR(255),
 project_description TEXT
);

CREATE TABLE experiments (
  experiment_id UUID PRIMARY KEY,
  protocol_id UUID,
  start_date DATE,
  end_date DATE
);

CREATE TABLE protocols (
  protocol_id UUID PRIMARY KEY,
  protocol_name VARCHAR(255),
  protocol_description TEXT
);

CREATE TABLE findings (
  finding_id UUID PRIMARY KEY,
  experiment_id UUID,
  finding_description TEXT,
  result VARCHAR(255)
);

CREATE TABLE projects (
  project_id UUID PRIMARY KEY,
  project_name VARCHAR(255),
  project_description TEXT
);

CREATE TABLE protocol (
    protocol_id INT PRIMARY KEY,
    protocol_name VARCHAR(255),
    description TEXT,
    create_date TIMESTAMP
);

CREATE TABLE researcher (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    email VARCHAR(255),
    institution VARCHAR(255)
);
