tables.table_schema:
CREATE TABLE stem_cell_research_data (
  research_id INT PRIMARY KEY,
  stem_cell_type VARCHAR(50),
  effectiveness_score FLOAT
);

CREATE TABLE StemCellTypes (
    id INT PRIMARY KEY,
    cell_type VARCHAR(50),
    effectiveness DECIMAL(5,2)
);

CREATE TABLE stem_cell_types (
  id SERIAL PRIMARY KEY,
  cell_type VARCHAR(50) NOT NULL,
  effectiveness FLOAT
);

CREATE TABLE research_data (
  id SERIAL PRIMARY KEY,
  researcher_name VARCHAR(100) NOT NULL,
  research_topic VARCHAR(200) NOT NULL,
  published_date DATE
);

CREATE TABLE research_results (
  id SERIAL PRIMARY KEY,
  research_id INT REFERENCES research_data(id),
  cell_type_id INT REFERENCES stem_cell_types(id),
  effectiveness_score FLOAT
);

CREATE TABLE research_data (
    research_id INT PRIMARY KEY,
    stem_cell_type VARCHAR(50),
    effectiveness_score INT
);

CREATE TABLE stem_cell_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50) UNIQUE
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    area_of_expertise VARCHAR(50)
);

CREATE TABLE stem_cell_types (
    id INT PRIMARY KEY,
    cell_type VARCHAR(50) NOT NULL
);

CREATE TABLE researchers (
    id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE research_centers (
    id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE research_tags (
    id INT PRIMARY KEY,
    tag VARCHAR(50) NOT NULL
);
