CREATE TABLE genetic_markers (
    marker_id INT PRIMARY KEY,
    marker_name VARCHAR(50),
    marker_type VARCHAR(50),
    marker_position INT
);

CREATE TABLE GeneticMarkers (
  marker_id INT PRIMARY KEY,
  marker_name VARCHAR(50) NOT NULL,
  marker_position INT,
  marker_type VARCHAR(20)
);

CREATE TABLE Genetic_Markers (
    marker_id INT PRIMARY KEY,
    gene_name VARCHAR(50),
    marker_type VARCHAR(50),
    chromosomal_location VARCHAR(20)
);
