tables.table_schema:
CREATE TABLE experiments (
	experiment_id INT PRIMARY KEY,
	protocol_id INT REFERENCES research_protocols(protocol_id),
	experiment_name STRING,
	experiment_date DATE
);

CREATE TABLE compliance_reports (
	report_id INT PRIMARY KEY,
	experiment_id INT REFERENCES experiments(experiment_id),
	compliance_status STRING,
	report_date DATE
);

CREATE TABLE violations (
	violation_id INT PRIMARY KEY,
	report_id INT REFERENCES compliance_reports(report_id),
	violation_description STRING,
	violation_date DATE
);

CREATE TABLE research_protocols (
  protocol_id INT PRIMARY KEY,
  protocol_name VARCHAR(255),
  researcher_name VARCHAR(255),
  ethically_compliant BOOLEAN
);

CREATE TABLE research_ethics_committee (
  committee_id INT PRIMARY KEY,
  committee_name VARCHAR(255),
  chairperson_name VARCHAR(255)
);

CREATE TABLE research_consent_forms (
  consent_id INT PRIMARY KEY,
  protocol_id INT,
  consent_text TEXT,
  date_created DATE
);

CREATE TABLE research_approved_studies (
  study_id INT PRIMARY KEY,
  protocol_id INT,
  study_name VARCHAR(255),
  approval_date DATE
);
