tables.table_schema:
CREATE TABLE stem_cell_procedures (
    procedure_id INT PRIMARY KEY,
    patient_id INT,
    procedure_date DATE,
    success_rate FLOAT
);

CREATE TABLE patient_data (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    patient_age INT,
    patient_blood_type VARCHAR(5)
);

CREATE TABLE transplantation_history (
    transplant_id INT PRIMARY KEY,
    procedure_id INT,
    patient_id INT,
    transplantation_date DATE
);

CREATE TABLE TransplantationOutcome (
  id SERIAL PRIMARY KEY,
  transplantation_id INT,
  outcome TEXT,
  remarks TEXT
);

CREATE TABLE stem_cell_donors (
  donor_id SERIAL PRIMARY KEY,
  donor_name VARCHAR(50) NOT NULL,
  donor_blood_type VARCHAR(5),
  donor_contact VARCHAR(20)
);

CREATE TABLE transplantation_centers (
  center_id SERIAL PRIMARY KEY,
  center_name VARCHAR(50) NOT NULL,
  location VARCHAR(100),
  director_name VARCHAR(50)
);
