tables.table_schema:
CREATE TABLE research_findings (
  id INT PRIMARY KEY,
  stem_cell_type VARCHAR(50),
  effect_type VARCHAR(50),
  finding_description TEXT
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(100),
  institution_name VARCHAR(100),
  email VARCHAR(100)
);

CREATE TABLE experiments (
  experiment_id INT PRIMARY KEY,
  research_id INT REFERENCES research_findings(id),
  researcher_id INT REFERENCES researchers(researcher_id),
  experiment_date DATE
);

CREATE TABLE StemCellTypes (
  id UUID PRIMARY KEY,
  cell_type_name TEXT NOT NULL,
  differentiation_status TEXT,
  culture_conditions TEXT
);

CREATE TABLE ResearchAuthors (
  id UUID PRIMARY KEY,
  author_name TEXT NOT NULL,
  affiliation TEXT,
  email TEXT UNIQUE
);

CREATE TABLE research_studies (
  study_id INT PRIMARY KEY,
  study_title VARCHAR(255),
  study_description TEXT,
  study_date DATE
);

CREATE TABLE cell_types (
  cell_id INT PRIMARY KEY,
  cell_type VARCHAR(50),
  isolation_method VARCHAR(100),
  associated_effects TEXT
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(100),
  researcher_email VARCHAR(100),
  affiliation VARCHAR(200)
);

CREATE TABLE experimental_groups (
  group_id INT PRIMARY KEY,
  group_name VARCHAR(50),
  treatment_protocol TEXT,
  group_description TEXT
);
