tables.table_schema:
CREATE TABLE devices (
  device_id UUID PRIMARY KEY,
  device_name VARCHAR,
  device_type VARCHAR,
  ip_address VARCHAR
);

CREATE TABLE network_devices (
  device_id UUID,
  network_id UUID,
  PRIMARY KEY (device_id, network_id)
);

CREATE TABLE network_devices (
    device_id SERIAL PRIMARY KEY,
    device_name VARCHAR(255) NOT NULL,
    device_ip VARCHAR(15) NOT NULL,
    device_status VARCHAR(10) DEFAULT 'Active'
);

CREATE TABLE network_logs (
    log_id SERIAL PRIMARY KEY,
    device_id INT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL,
    log_description TEXT NOT NULL
);

CREATE TABLE network_traffic (
    traffic_id SERIAL PRIMARY KEY,
    device_id INT NOT NULL,
    traffic_timestamp TIMESTAMP NOT NULL,
    traffic_bytes INT NOT NULL
);

CREATE TABLE network_alerts (
    alert_id SERIAL PRIMARY KEY,
    device_id INT NOT NULL,
    alert_timestamp TIMESTAMP NOT NULL,
    alert_type VARCHAR(50) NOT NULL
);

CREATE TABLE network_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    user_email VARCHAR(100) UNIQUE NOT NULL
);
