tables.table_schema:
CREATE TABLE EquipmentFailureData (
   id INT PRIMARY KEY,
   equipment_id INT,
   failure_type VARCHAR(255),
   failure_timestamp TIMESTAMP
);

CREATE TABLE PreventiveMaintenanceSchedule (
   id INT PRIMARY KEY,
   equipment_id INT,
   maintenance_type VARCHAR(255),
   scheduled_date DATE
);

CREATE TABLE RemoteMonitoringSystem (
   id INT PRIMARY KEY,
   equipment_id INT,
   monitoring_status VARCHAR(255),
   last_updated TIMESTAMP
);

CREATE TABLE equipment_failure_data (
  equipment_id INT,
  failure_time TIMESTAMP,
  failure_description TEXT,
  PRIMARY KEY (equipment_id)
);

CREATE TABLE remote_monitoring_system (
  system_id INT,
  system_name VARCHAR(255),
  location VARCHAR(255),
  PRIMARY KEY (system_id)
);

CREATE TABLE equipment_failure_data (
  equipment_id INT,
  failure_date DATE,
  failure_description TEXT
);

CREATE TABLE preventive_maintenance (
  maintenance_id INT,
  equipment_id INT,
  schedule_date DATE,
  maintenance_type TEXT
);

CREATE TABLE monitoring_system (
  system_id INT,
  equipment_id INT,
  status TEXT,
  last_check_date DATE
);

CREATE TABLE maintenance_schedule (
  schedule_id INT,
  maintenance_id INT,
  start_date DATE,
  end_date DATE
);

CREATE TABLE equipment (
  equipment_id INT,
  equipment_name TEXT,
  equipment_type TEXT
);
