tables.table_schema:
CREATE TABLE performance_data (
    data_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    source_id INT,
    energy_output DECIMAL
);

CREATE TABLE energy_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50),
    source_type VARCHAR(50)
);

CREATE TABLE monitoring_system (
    system_id INT PRIMARY KEY,
    system_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE SolarPanels (
    id INT PRIMARY KEY,
    panel_name VARCHAR(50),
    location VARCHAR(100),
    efficiency FLOAT
);

CREATE TABLE WindTurbines (
    id INT PRIMARY KEY,
    turbine_name VARCHAR(50),
    location VARCHAR(100),
    blade_length FLOAT
);

CREATE TABLE HydroPlants (
    id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    location VARCHAR(100),
    capacity FLOAT
);

CREATE TABLE EnergyProduction (
    id INT PRIMARY KEY,
    source_id INT,
    production_date DATE,
    production_amount FLOAT
);
