tables.table_schema:
CREATE TABLE telehealth_billing_data (
  patient_id INT,
  patient_name TEXT,
  billing_amount DECIMAL,
  payment_status TEXT
);

CREATE TABLE telehealth_payment_data (
  payment_id INT,
  patient_id INT,
  payment_amount DECIMAL,
  payment_date DATE
);

CREATE TABLE telehealth_billing (
  patient_id INT NOT NULL,
  service_date DATE NOT NULL,
  service_description VARCHAR(255) NOT NULL,
  service_cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE telehealth_payment (
  payment_id INT NOT NULL,
  patient_id INT NOT NULL,
  payment_date DATE NOT NULL,
  payment_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE telehealth_invoice (
  invoice_id INT NOT NULL,
  patient_id INT NOT NULL,
  invoice_date DATE NOT NULL,
  total_amount_due DECIMAL(10, 2) NOT NULL
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50) NOT NULL,
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE doctors (
    doctor_id INT PRIMARY KEY,
    doctor_name VARCHAR(50) NOT NULL,
    specialty VARCHAR(50)
);

CREATE TABLE payments (
    payment_id INT PRIMARY KEY,
    patient_id INT,
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE telehealth_patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    patient_email VARCHAR(50),
    date_of_birth DATE
);

CREATE TABLE telehealth_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    service_description TEXT,
    service_price DECIMAL(10, 2)
);

CREATE TABLE telehealth_invoices (
    invoice_id INT PRIMARY KEY,
    appointment_id INT,
    total_amount DECIMAL(10, 2),
    status VARCHAR(20)
);

CREATE TABLE telehealth_payments (
    payment_id INT PRIMARY KEY,
    invoice_id INT,
    payment_date DATE,
    amount_paid DECIMAL(10, 2)
);
