tables.table_schema:
CREATE TABLE psychiatry_database (
	patient_id UUID PRIMARY KEY,
	patient_name VARCHAR(50) NOT NULL,
	diagnosis TEXT,
	treatment_plan TEXT
);

CREATE TABLE health_records (
	patient_id UUID PRIMARY KEY,
	patient_name VARCHAR(50) NOT NULL,
	blood_pressure DECIMAL(5,2),
	heart_rate INTEGER
);

CREATE TABLE telepsychiatry_patient (
	patient_id INT PRIMARY KEY,
	patient_name VARCHAR(50),
	date_of_birth DATE,
	psychiatrist_id INT
);

CREATE TABLE psychiatrist (
	psychiatrist_id INT PRIMARY KEY,
	psychiatrist_name VARCHAR(50),
	specialty VARCHAR(50),
	hospital_id INT
);

CREATE TABLE hospital (
	hospital_id INT PRIMARY KEY,
	hospital_name VARCHAR(50),
	location VARCHAR(50)
);

CREATE TABLE patient_care_plan (
	care_plan_id INT PRIMARY KEY,
	patient_id INT,
	care_plan_details TEXT
);
