tables.table_schema:
CREATE TABLE rural_healthcare_availability (
    id INT PRIMARY KEY,
    technology_name VARCHAR(255),
    availability_status BOOLEAN
);

CREATE TABLE rural_healthcare_usage (
    id INT PRIMARY KEY,
    technology_name VARCHAR(255),
    usage_duration INT
);

CREATE TABLE rural_healthcare_location (
    id INT PRIMARY KEY,
    location_name VARCHAR(255),
    region VARCHAR(255)
);

CREATE TABLE rural_healthcare_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    technology_available BOOLEAN
);
