tables.table_schema:
CREATE TABLE specialists (
    specialist_id UUID PRIMARY KEY,
    name TEXT,
    area_of_expertise TEXT,
    email TEXT
);

CREATE TABLE oncologists (
    oncologist_id INT PRIMARY KEY,
    oncologist_name VARCHAR(100) NOT NULL,
    specialty VARCHAR(50),
    years_of_experience INT
);

CREATE TABLE TumorImages (
    image_id UUID PRIMARY KEY,
    image_data BYTEA,
    patient_id UUID,
    scan_date DATE
);

CREATE TABLE TumorScans (
    scan_id UUID PRIMARY KEY,
    scan_data BYTEA,
    patient_id UUID,
    scan_date DATE
);

CREATE TABLE Patients (
    patient_id UUID PRIMARY KEY,
    patient_name VARCHAR(50),
    birth_date DATE,
    diagnosis VARCHAR(100)
);

CREATE TABLE Specialists (
    specialist_id UUID PRIMARY KEY,
    specialist_name VARCHAR(50),
    specialization VARCHAR(100)
);

CREATE TABLE image (
  id UUID PRIMARY KEY,
  patient_id UUID,
  image_data BYTEA
);

CREATE TABLE scan (
  id UUID PRIMARY KEY,
  patient_id UUID,
  scan_date TIMESTAMP,
  scan_details TEXT
);

CREATE TABLE tumor (
  id UUID PRIMARY KEY,
  scan_id UUID,
  tumor_size DECIMAL,
  tumor_location TEXT
);

CREATE TABLE specialist (
  id UUID PRIMARY KEY,
  name TEXT,
  specialization TEXT
);

CREATE TABLE analysis (
  id UUID PRIMARY KEY,
  tumor_id UUID,
  specialist_id UUID,
  analysis_result TEXT
);
