tables.table_schema:
CREATE TABLE teleoncology_teams (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  team_location VARCHAR(50) 
);

CREATE TABLE team_members (
  member_id INT PRIMARY KEY,
  member_name VARCHAR,
  member_role VARCHAR,
  team_id INT REFERENCES multidisciplinary_meetings(meeting_id)
);

CREATE TABLE teams (
 team_id SERIAL PRIMARY KEY,
 team_name VARCHAR(50) NOT NULL,
 location VARCHAR(100) NOT NULL
);

CREATE TABLE patients (
 patient_id SERIAL PRIMARY KEY,
 patient_name VARCHAR(50) NOT NULL,
 diagnosis VARCHAR(100) NOT NULL,
 location VARCHAR(100) NOT NULL
);

CREATE TABLE TeleoncologyTeams (
	eam_id INT PRIMARY KEY,
	eam_name VARCHAR(255),
	eam_location VARCHAR(255),
	eam_specialty VARCHAR(255)
);
