tables.table_schema:
CREATE TABLE Cardiologists (
  cardiologist_id INT PRIMARY KEY,
  name VARCHAR(100),
  specialty VARCHAR(100),
  email VARCHAR(100)
);

CREATE TABLE Cardiologists (
    id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    specialty VARCHAR(50) NOT NULL
);

CREATE TABLE HIPAACompliance (
    id UUID PRIMARY KEY,
    information_security_officer VARCHAR(50) NOT NULL,
    compliance_status BOOLEAN NOT NULL,
    compliance_date DATE NOT NULL
);
