tables.table_schema:
CREATE TABLE Outcomes (
  outcome_id INT PRIMARY KEY,
  treatment_id INT,
  outcome_description TEXT,
  outcome_date DATE
);

CREATE TABLE ResearchData (
  data_id INT PRIMARY KEY,
  outcome_id INT,
  patient_id INT,
  research_notes TEXT
);

CREATE TABLE doctors (
  doctor_id INT PRIMARY KEY,
  doctor_name VARCHAR,
  specialization VARCHAR,
  experience_years INT
);

CREATE TABLE outcomes (
  outcome_id INT PRIMARY KEY,
  appointment_id INT,
  outcome_details VARCHAR
);
