tables.table_schema:
CREATE TABLE TelecardiologyPatients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    patient_age INT,
    insurance_provider VARCHAR(50)
);

CREATE TABLE TelecardiologyBilling (
    bill_id INT PRIMARY KEY,
    patient_id INT,
    bill_amount DECIMAL(10, 2),
    bill_date DATE
);

CREATE TABLE telecardiology_patient (
  patient_id UUID PRIMARY KEY,
  name VARCHAR(255),
  dob DATE,
  address TEXT
);

CREATE TABLE telecardiology_services (
  service_id UUID PRIMARY KEY,
  service_name VARCHAR(255),
  description TEXT,
  cost DECIMAL(10, 2)
);
